mapper.select:
select
b.driver_id driverId,
b.user_name driverName,
phone,
b.id_card idCard,
b.user_acct driverAcct
from t_driver as a inner join t_sys_user as b
on driver_id = user_id
where is_delete = 0

    and driver_id = #{driverId}


    and driver_name = #{driverName}


    and province_value = #{provinceValue}


    and city_value = #{cityValue}


    and county_value = #{countyValue}
